def sum_of_digits(n):
    return sum(int(digit) for digit in str(n))

def find_max_sum_of_digits(x):
    str_x = str(x)
    length = len(str_x)

    # Start with the number itself
    max_sum = sum_of_digits(x)
    result = x

    # Try to form the largest number with maximum sum of digits
    for i in range(length):
        if str_x[i] == '0':
            continue

        # Form a new number by reducing the current digit by 1 and setting all following digits to 9
        new_number = int(str_x[:i] + str(int(str_x[i]) - 1) + '9' * (length - i - 1))

        if new_number > 0:
            current_sum = sum_of_digits(new_number)
            if current_sum > max_sum or (current_sum == max_sum and new_number > result):
                max_sum = current_sum
                result = new_number

    return result

x = int(input())
print(find_max_sum_of_digits(x))

def is_lucky(year):
    non_zero_digits = [digit for digit in str(year) if digit != '0']
    return len(non_zero_digits) <= 1

def next_lucky_year(current_year):
    year = current_year + 1
    while not is_lucky(year):
        year += 1
    return year - current_year

