def min_seconds_to_close_tabs(n, pos, l, r):
    if l == 1 and r == n:
        return 0
    elif l == 1:
        return abs(pos - r) + 1
    elif r == n:
        return abs(pos - l) + 1
    else:
        return min(abs(pos - l), abs(pos - r)) + (r - l) + 2

