def min_tiles_to_draw(tiles):
    # Parse the input tiles
    tile_counts = {}
    for tile in tiles:
        if tile in tile_counts:
            tile_counts[tile] += 1
        else:
            tile_counts[tile] = 1

    # Check for koutsu (triplet)
    for count in tile_counts.values():
        if count == 3:
            return 0

    # Check for shuntsu (sequence)
    suits = {'m': [], 'p': [], 's': []}
    for tile in tiles:
        number = int(tile[0])
        suit = tile[1]
        suits[suit].append(number)

    for suit in suits.values():
        suit.sort()
        if len(suit) == 3:
            if suit[2] - suit[0] == 2 and suit[1] - suit[0] == 1:
                return 0

    # Check for possible shuntsu with one extra tile
    for suit in suits.values():
        if len(suit) == 2:
            if suit[1] - suit[0] <= 2:
                return 1

    # If no koutsu or shuntsu is possible, return 2
    return 2

