def max_beauty(n, trophies):
    # Find all segments of golden trophies
    segments = []
    current_length = 0
    for i in range(n):
        if trophies[i] == 'G':
            current_length += 1
        else:
            if current_length > 0:
                segments.append(current_length)
            current_length = 0
    if current_length > 0:
        segments.append(current_length)

    # If there are no golden trophies
    if not segments:
        return 0

    # If there is only one segment of golden trophies
    if len(segments) == 1:
        return min(segments[0] + 1, n)

    # Calculate the maximum beauty with at most one swap
    max_beauty = 0
    for i in range(len(segments) - 1):
        # Check if there is exactly one silver trophy between two segments
        if trophies[trophies.index('G' * segments[i]) + segments[i]] == 'S' and trophies[trophies.index('G' * segments[i]) + segments[i] + 1] == 'G':
            max_beauty = max(max_beauty, segments[i] + segments[i + 1] + 1)
        else:
            max_beauty = max(max_beauty, segments[i] + 1, segments[i + 1] + 1)

    return max(max_beauty, max(segments))

