def is_stack_sortable(a):
    s = []
    b = []
    for num in a:
        while s and (not b or s[-1] < b[-1]):
            b.append(s.pop())
        s.append(num)
    while s:
        b.append(s.pop())
    return b == sorted(b)

def solve(n, k, p):
    remaining = set(range(1, n + 1)) - set(p)
    remaining = sorted(remaining, reverse=True)

    for i in range(len(remaining)):
        candidate = p + remaining[:i+1] + sorted(remaining[i+1:], reverse=True)
        if is_stack_sortable(candidate):
            return candidate
    return -1

