def is_valid_sequence(n, stats):
    for i in range(n):
        p, c = stats[i]
        if c > p:
            return False
        if i > 0:
            prev_p, prev_c = stats[i-1]
            if p < prev_p or c < prev_c or (p - prev_p) < (c - prev_c):
                return False
    return True

