def is_palindrome(time):
    return time == time[::-1]

def increment_time(hh, mm):
    mm += 1
    if mm == 60:
        mm = 0
        hh += 1
        if hh == 24:
            hh = 0
    return hh, mm

def format_time(hh, mm):
    return f"{hh:02}:{mm:02}"

def min_minutes_to_palindrome(time):
    hh, mm = map(int, time.split(':'))
    minutes = 0
    while True:
        formatted_time = format_time(hh, mm)
        if is_palindrome(formatted_time.replace(':', '')):
            return minutes
        hh, mm = increment_time(hh, mm)
        minutes += 1

