def generate_matrix(n, k):
    if k > n * n:
        return -1

    matrix = [[0] * n for _ in range(n)]

    for i in range(n):
        if k <= 0:
            break
        if k >= 2 and i != n - 1:
            matrix[i][i] = 1
            matrix[i][i + 1] = 1
            matrix[i + 1][i] = 1
            k -= 2
        elif k == 1:
            matrix[i][i] = 1
            k -= 1

    if k > 0:
        return -1

    return matrix

