def is_valid_journey(n, journey):
    position = 0  # Start at the North Pole

    for t, dir in journey:
        if dir == "North":
            if position == 0:
                return "NO"
            position -= t
        elif dir == "South":
            if position == 20000:
                return "NO"
            position += t
        elif dir in ["West", "East"]:
            if position == 0 or position == 20000:
                return "NO"

        if position < 0 or position > 20000:
            return "NO"

    return "YES" if position == 0 else "NO"

