def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def find_common_elements(a1, b1, a2, b2, L, R):
    g = gcd(a1, a2)
    if (b2 - b1) % g != 0:
        return 0

    lcm = (a1 // g) * a2
    start = max(L, min(b1, b2))
    end = min(R, max(b1, b2) + lcm - 1)

    if start > end:
        return 0

    count = 0
    for x in range(start, end + 1):
        if (x - b1) % a1 == 0 and (x - b2) % a2 == 0:
            count += 1

    return count

