def is_berland_flag(n, m, field):
    def check_stripes(lines):
        if len(lines) % 3 != 0:
            return False
        stripe_height = len(lines) // 3
        colors = set()
        for i in range(3):
            stripe = lines[i * stripe_height:(i + 1) * stripe_height]
            color = stripe[0]
            if any(row != color for row in stripe):
                return False
            colors.add(color)
        return len(colors) == 3

    def check_vertical_stripes():
        transposed_field = [''.join(row) for row in zip(*field)]
        return check_stripes(transposed_field)

    def check_horizontal_stripes():
        return check_stripes(field)

    if check_horizontal_stripes() or check_vertical_stripes():
        return "YES"
    return "NO"

