def find_coordinates(n):
    if n == 0:
        return (0, 0)

    # Directions in the order of the spiral
    directions = [(1, 0), (0, 1), (-1, 1), (-1, 0), (0, -1), (1, -1)]

    # Initialize starting point
    x, y = 0, 0
    step = 1
    move_count = 0

    while move_count < n:
        for direction in directions:
            for _ in range(step):
                if move_count == n:
                    return (x, y)
                x += direction[0]
                y += direction[1]
                move_count += 1
            if direction == directions[2] or direction == directions[5]:
                step += 1

    return (x, y)

