def longest_non_palindrome_substring_length(s):
    n = len(s)
    if not is_palindrome(s):
        return n
    for length in range(n-1, 0, -1):
        for start in range(n - length + 1):
            substring = s[start:start + length]
            if not is_palindrome(substring):
                return length
    return 0

