def find_kth_largest(n, m, k):
    def count_less_equal(x):
        count = 0
        for i in range(1, n + 1):
            count += min(x // i, m)
        return count

    left, right = 1, n * m
    while left < right:
        mid = (left + right) // 2
        if count_less_equal(mid) < k:
            left = mid + 1
        else:
            right = mid
    return left

