def find_teams(n):
    results = []

    def count_games(teams):
        games = 0
        while teams % 2 == 0:
            games += teams // 2
            teams //= 2
        if teams > 1:
            games += teams * (teams - 1) // 2
        return games

    for teams in range(1, n + 1):
        if count_games(teams) == n:
            results.append(teams)

    if results:
        for result in results:
            print(result)
    else:
        print(-1)

