def calculate_area(vertices):
    if len(vertices) == 1:
        return -1
    elif len(vertices) == 2:
        x1, y1 = vertices[0]
        x2, y2 = vertices[1]
        return abs(x2 - x1) * abs(y2 - y1)
    elif len(vertices) == 3:
        x_coords = set()
        y_coords = set()
        for x, y in vertices:
            x_coords.add(x)
            y_coords.add(y)
        if len(x_coords) == 2 and len(y_coords) == 2:
            x1, x2 = x_coords
            y1, y2 = y_coords
            return abs(x2 - x1) * abs(y2 - y1)
        else:
            return -1
    elif len(vertices) == 4:
        x_coords = set()
        y_coords = set()
        for x, y in vertices:
            x_coords.add(x)
            y_coords.add(y)
        if len(x_coords) == 2 and len(y_coords) == 2:
            x1, x2 = x_coords
            y1, y2 = y_coords
            return abs(x2 - x1) * abs(y2 - y1)
        else:
            return -1

