def count_valid_decks(n, k, cards):
    from math import gcd
    from functools import reduce

    def lcm(a, b):
        return a * b // gcd(a, b)

    def product_mod_k(arr, k):
        result = 1
        for num in arr:
            result = (result * num) % k
            if result == 0:
                return 0
        return result

    total_product = reduce(lambda x, y: x * y, cards)
    if total_product % k == 0:
        return n * (n + 1) // 2

    valid_ways = 0
    prefix_product = [1] * (n + 1)
    suffix_product = [1] * (n + 1)

    for i in range(n):
        prefix_product[i + 1] = (prefix_product[i] * cards[i]) % k

    for i in range(n - 1, -1, -1):
        suffix_product[i] = (suffix_product[i + 1] * cards[i]) % k

    for x in range(n):
        for y in range(n - x):
            if (prefix_product[x] * suffix_product[n - y]) % k == 0:
                valid_ways += 1

    return valid_ways

