def min_length_to_reach_target(n, operations, x, y):
    # Calculate the final position after all operations
    final_x, final_y = 0, 0
    for op in operations:
        if op == 'U':
            final_y += 1
        elif op == 'D':
            final_y -= 1
        elif op == 'L':
            final_x -= 1
        elif op == 'R':
            final_x += 1

    # If the final position is already the target, no changes are needed
    if final_x == x and final_y == y:
        return 0

    # Calculate the difference needed to reach the target
    dx, dy = x - final_x, y - final_y

    # If the difference is odd, it's impossible to reach the target
    if (dx + dy) % 2 != 0:
        return -1

    # Use a sliding window to find the minimum length of subsegment to change
    min_length = float('inf')
    current_x, current_y = 0, 0
    left = 0

    for right in range(n):
        if operations[right] == 'U':
            current_y += 1
        elif operations[right] == 'D':
            current_y -= 1
        elif operations[right] == 'L':
            current_x -= 1
        elif operations[right] == 'R':
            current_x += 1

        while left <= right and abs(current_x - dx) + abs(current_y - dy) <= (right - left + 1):
            min_length = min(min_length, right - left + 1)
            if operations[left] == 'U':
                current_y -= 1
            elif operations[left] == 'D':
                current_y += 1
            elif operations[left] == 'L':
                current_x += 1
            elif operations[left] == 'R':
                current_x -= 1
            left += 1

    return min_length if min_length != float('inf') else -1

