        if balance in balance_counts:
            balance_counts[balance] += 1
        else:
            balance_counts[balance] = 1

    total_balance = balance
    if total_balance == 0:
        if x in balance_counts:
            return -1
        else:
            return 0

    result = 0
    current_balance = 0
    for i in range(n):
        if current_balance == x:
            result += 1
        if total_balance != 0 and (x - current_balance) % total_balance == 0 and (x - current_balance) // total_balance >= 0:
            result += 1
        if s[i] == '0':
            current_balance += 1
        else:
            current_balance -= 1

    return result

