def literature_lesson(n, m, k, x, y):
    if n == 1:
        max_questions = (k + m - 1) // m
        min_questions = k // m
        sergei_questions = (k + y - 1) // m
    else:
        full_cycle = 2 * n - 2
        full_cycle_questions = full_cycle * m
        full_cycles_completed = k // full_cycle_questions
        remaining_questions = k % full_cycle_questions

        max_questions = full_cycles_completed * 2
        min_questions = full_cycles_completed

        if remaining_questions > 0:
            max_questions += 1
        if remaining_questions > n * m:
            max_questions += 1

        sergei_questions = full_cycles_completed * 2
        if remaining_questions > 0:
            if (x - 1) * m + y <= remaining_questions:
                sergei_questions += 1
            if remaining_questions > n * m and (2 * n - x - 1) * m + y <= remaining_questions - n * m:
                sergei_questions += 1

    print(max_questions, min_questions, sergei_questions)

