def is_prime(num):
    if num <= 1:
        return False
    if num <= 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    while i * i <= num:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True

def find_primes(n):
    if is_prime(n):
        return [n]
    for i in range(2, n):
        if is_prime(i) and is_prime(n - i):
            return [i, n - i]
    for i in range(2, n):
        if is_prime(i):
            for j in range(2, n - i):
                if is_prime(j) and is_prime(n - i - j):
                    return [i, j, n - i - j]

