MOD = 10**9 + 7

def count_playlists(n, T, songs):
    dp = [[[[0] * 4 for _ in range(T + 1)] for _ in range(1 << n)] for _ in range(n + 1)]
    dp[0][0][0][0] = 1

    for i in range(n):
        for mask in range(1 << n):
            for t in range(T + 1):
                for last_genre in range(4):
                    if dp[i][mask][t][last_genre] == 0:
                        continue
                    for j in range(n):
                        if mask & (1 << j):
                            continue
                        song_time, song_genre = songs[j]
                        if t + song_time > T or song_genre == last_genre:
                            continue
                        new_mask = mask | (1 << j)
                        dp[i + 1][new_mask][t + song_time][song_genre] += dp[i][mask][t][last_genre]
                        dp[i + 1][new_mask][t + song_time][song_genre] %= MOD

    result = 0
    for i in range(1, n + 1):
        for last_genre in range(1, 4):
            result = (result + dp[i][(1 << n) - 1][T][last_genre]) % MOD

    return result

