def mod_inverse(x, mod):
    return pow(x, mod - 2, mod)

def expected_length(m):
    phi = list(range(m + 1))
    for i in range(2, m + 1):
        if phi[i] == i:
            for j in range(i, m + 1, i):
                phi[j] = phi[j] * (i - 1) // i

    P = sum(phi[1:])
    Q = m

    Q_inv = mod_inverse(Q, MOD)
    result = (P * Q_inv) % MOD
    return result

