def count_good_pairs(l, r, x, y):
    if y % x != 0:
        return 0

    count = 0
    product = y // x

    for a in range(1, int(math.sqrt(product)) + 1):
        if product % a == 0:
            b = product // a
            a *= x
            b *= x
            if l <= a <= r and l <= b <= r and math.gcd(a, b) == x and (a * b) // math.gcd(a, b) == y:
                if a == b:
                    count += 1
                else:
