import math

def lcm(x, y):
    return x * y // math.gcd(x, y)

def find_min_k(a, b):
    if a == b:
        return 0

    diff = abs(a - b)
    min_lcm = float('inf')
    best_k = 0

    for i in range(1, int(math.sqrt(diff)) + 1):
        if diff % i == 0:
            for k in [i, diff // i]:
                new_a = a + k - a % k
                new_b = b + k - b % k
                current_lcm = lcm(new_a, new_b)
                if current_lcm < min_lcm or (current_lcm == min_lcm and k < best_k):
                    min_lcm = current_lcm
                    best_k = k

    return best_k

