def gcd(x, y):
    while y:
        x, y = y, x % y
    return x

def can_be_equal(a1, b1, a2, b2):
    area1 = a1 * b1
    area2 = a2 * b2
    if area1 == area2:
        return 0, (a1, b1), (a2, b2)

    g1 = gcd(a1, b1)
    g2 = gcd(a2, b2)

    a1 //= g1
    b1 //= g1
    a2 //= g2
    b2 //= g2

    if a1 * b1 != a2 * b2:
        return -1, None, None

    return 1, (a1 * g1, b1 * g1), (a2 * g2, b2 * g2)

