from collections import deque

def puzzle_to_tuple(puzzle):
    return tuple(tuple(row) for row in puzzle)

def find_empty(puzzle):
    for i in range(2):
        for j in range(2):
            if puzzle[i][j] == 'X':
                return (i, j)

def get_neighbors(pos):
    i, j = pos
    neighbors = []
    if i > 0: neighbors.append((i-1, j))
    if i < 1: neighbors.append((i+1, j))
    if j > 0: neighbors.append((i, j-1))
    if j < 1: neighbors.append((i, j+1))
    return neighbors

def bfs(start, target):
    queue = deque([start])
    visited = set()
    visited.add(start)

    while queue:
        current = queue.popleft()
        if current == target:
            return True

        empty_pos = find_empty(current)
        for neighbor in get_neighbors(empty_pos):
            new_puzzle = [list(row) for row in current]
            ni, nj = neighbor
            ei, ej = empty_pos
            new_puzzle[ei][ej], new_puzzle[ni][nj] = new_puzzle[ni][nj], new_puzzle[ei][ej]
            new_tuple = puzzle_to_tuple(new_puzzle)
            if new_tuple not in visited:
                visited.add(new_tuple)
                queue.append(new_tuple)

    return False

