def max_value_in_k_paths(n, k):
    def count_occurrences(y):
        count = 0
        for i in range(1, n + 1):
            x = i
            while x >= y:
                if x == y:
                    count += 1
                    break
                if x % 2 == 0:
                    x //= 2
                else:
                    x -= 1
        return count

    left, right = 1, n
    result = 1

    while left <= right:
        mid = (left + right) // 2
        if count_occurrences(mid) >= k:
            result = mid
            left = mid + 1
        else:
            right = mid - 1

    return result

