def find_pocket(n, m, x, y, vx, vy):
    if vx == 0:
        if vy == 0:
            return -1
        if vy == 1:
            return (0, m) if x == 0 else (n, m)
        if vy == -1:
            return (0, 0) if x == 0 else (n, 0)
    if vy == 0:
        if vx == 1:
            return (n, 0) if y == 0 else (n, m)
        if vx == -1:
            return (0, 0) if y == 0 else (0, m)

    if vx == 1 and vy == 1:
        t1 = (n - x) // vx
        t2 = (m - y) // vy
        if t1 == t2:
            return (n, m)
        return -1
    if vx == 1 and vy == -1:
        t1 = (n - x) // vx
        t2 = y // -vy
        if t1 == t2:
            return (n, 0)
        return -1
    if vx == -1 and vy == 1:
        t1 = x // -vx
        t2 = (m - y) // vy
        if t1 == t2:
            return (0, m)
        return -1
    if vx == -1 and vy == -1:
        t1 = x // -vx
        t2 = y // -vy
        if t1 == t2:
            return (0, 0)
        return -1

