def find_frame(n, m, screen):
    min_row, max_row = n, -1
    min_col, max_col = m, -1

    # Find the bounding box of the white pixels
    for i in range(n):
        for j in range(m):
            if screen[i][j] == 'w':
                min_row = min(min_row, i)
                max_row = max(max_row, i)
                min_col = min(min_col, j)
                max_col = max(max_col, j)

    # Calculate the size of the frame
    frame_size = max(max_row - min_row + 1, max_col - min_col + 1)

    # Check if the frame fits within the screen
    if frame_size > n or frame_size > m:
        return -1

    # Check if the frame can be placed without overlapping existing white pixels
    for i in range(min_row, min_row + frame_size):
        for j in range(min_col, min_col + frame_size):
            if screen[i][j] == 'w' and not (i == min_row or i == min_row + frame_size - 1 or j == min_col or j == min_col + frame_size - 1):
                return -1

    # Create the output screen with the frame
    output_screen = [list(row) for row in screen]
    for i in range(min_row, min_row + frame_size):
        for j in range(min_col, min_col + frame_size):
            if output_screen[i][j] == '.':
                if i == min_row or i == min_row + frame_size - 1 or j == min_col or j == min_col + frame_size - 1:
                    output_screen[i][j] = '+'

    return output_screen

