from collections import deque

def get_moves(piece, x, y, N):
    moves = []
    if piece == 'knight':
        knight_moves = [(-2, -1), (-1, -2), (1, -2), (2, -1), (2, 1), (1, 2), (-1, 2), (-2, 1)]
        for dx, dy in knight_moves:
            nx, ny = x + dx, y + dy
            if 0 <= nx < N and 0 <= ny < N:
                moves.append((nx, ny))
    elif piece == 'bishop':
        for d in range(1, N):
            for dx, dy in [(-d, -d), (-d, d), (d, -d), (d, d)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < N and 0 <= ny < N:
                    moves.append((nx, ny))
    elif piece == 'rook':
        for i in range(N):
            if i != x:
                moves.append((i, y))
            if i != y:
                moves.append((x, i))
    return moves

def bfs(start, end, N, board):
    queue = deque([(start[0], start[1], 0, 'knight', 0), (start[0], start[1], 0, 'bishop', 0), (start[0], start[1], 0, 'rook', 0)])
    visited = set()
    visited.add((start[0], start[1], 'knight'))
    visited.add((start[0], start[1], 'bishop'))
    visited.add((start[0], start[1], 'rook'))

    while queue:
        x, y, steps, piece, replacements = queue.popleft()

        if (x, y) == end:
            return steps, replacements

        for next_piece in ['knight', 'bishop', 'rook']:
            if next_piece != piece:
                if (x, y, next_piece) not in visited:
                    visited.add((x, y, next_piece))
                    queue.append((x, y, steps + 1, next_piece, replacements + 1))

        for nx, ny in get_moves(piece, x, y, N):
            if (nx, ny, piece) not in visited:
                visited.add((nx, ny, piece))
                queue.append((nx, ny, steps + 1, piece, replacements))

    return float('inf'), float('inf')

def solve_chess_puzzle(N, board):
    positions = {}
    for i in range(N):
        for j in range(N):
            positions[board[i][j]] = (i, j)

    total_steps = 0
    total_replacements = 0

    for num in range(1, N * N):
        start = positions[num]
        end = positions[num + 1]
        steps, replacements = bfs(start, end, N, board)
        total_steps += steps
        total_replacements += replacements

    print(total_steps, total_replacements)

