def bad_luck_island(r, s, p):
    dp = [[[0.0 for _ in range(p + 1)] for _ in range(s + 1)] for _ in range(r + 1)]
    dp[r][s][p] = 1.0

    for i in range(r, -1, -1):
        for j in range(s, -1, -1):
            for k in range(p, -1, -1):
                total_pairs = i * j + j * k + k * i
                if total_pairs == 0:
                    continue
                if i > 0 and j > 0:
                    dp[i][j - 1][k] += dp[i][j][k] * (i * j) / total_pairs
                if j > 0 and k > 0:
                    dp[i][j][k - 1] += dp[i][j][k] * (j * k) / total_pairs
                if k > 0 and i > 0:
                    dp[i - 1][j][k] += dp[i][j][k] * (k * i) / total_pairs

    pr = sum(dp[i][0][0] for i in range(r + 1))
    ps = sum(dp[0][j][0] for j in range(s + 1))
    pp = sum(dp[0][0][k] for k in range(p + 1))

    return pr, ps, pp

