def decode_genome(n, s):
    if n % 4 != 0:
        return "==="

    count = {'A': 0, 'C': 0, 'G': 0, 'T': 0, '?': 0}
    for char in s:
        count[char] += 1

    required_each = n // 4
    for nucleotide in 'ACGT':
        if count[nucleotide] > required_each:
            return "==="

    for nucleotide in 'ACGT':
        count['?'] -= (required_each - count[nucleotide])
        count[nucleotide] = required_each

    if count['?'] != 0:
        return "==="

    result = []
    for char in s:
        if char == '?':
            for nucleotide in 'ACGT':
                if count[nucleotide] > 0:
                    result.append(nucleotide)
                    count[nucleotide] -= 1
                    break
        else:
            result.append(char)

    return ''.join(result)

