def count_ways(n, p, colors):
    # Count the number of uncolored pieces
    uncolored_count = colors.count(-1)

    # If there are no uncolored pieces, we need to check the parity directly
    if uncolored_count == 0:
        score = 0
        for i in range(n):
            for j in range(i + 1, n):
                if colors[i] != colors[j]:
                    score += 1
        return 1 if score % 2 == p else 0

    # If there are uncolored pieces, we need to consider all possible colorings
    total_ways = 0
    for mask in range(1 << uncolored_count):
        new_colors = colors[:]
        idx = 0
        for i in range(n):
            if new_colors[i] == -1:
                new_colors[i] = (mask >> idx) & 1
                idx += 1

        score = 0
        for i in range(n):
            for j in range(i + 1, n):
                if new_colors[i] != new_colors[j]:
                    score += 1

        if score % 2 == p:
            total_ways += 1

    return total_ways % MOD

