def is_cyclic(graph, n):
    visited = [False] * n
    rec_stack = [False] * n

    def dfs(v):
        visited[v] = True
        rec_stack[v] = True
        for neighbor in graph[v]:
            if not visited[neighbor]:
                if dfs(neighbor):
                    return True
            elif rec_stack[neighbor]:
                return True
        rec_stack[v] = False
        return False

    for node in range(n):
        if not visited[node]:
            if dfs(node):
                return True
    return False

def can_be_acyclic(n, m, edges):
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u-1].append(v-1)

    if not is_cyclic(graph, n):
        return "YES"

    for u, v in edges:
        graph[u-1].remove(v-1)
        if not is_cyclic(graph, n):
            return "YES"
        graph[u-1].append(v-1)

    return "NO"

