    if not unlucky_years:
        return r - l + 1

    max_length = 0
    if l < unlucky_years[0]:
        max_length = unlucky_years[0] - l

    for i in range(1, len(unlucky_years)):
        max_length = max(max_length, unlucky_years[i] - unlucky_years[i-1] - 1)

    if r > unlucky_years[-1]:
