def decode_message(encoded_str, k):
    n = len(encoded_str)
    dp = [[False] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = True

    for i in range(1, n + 1):
        for j in range(k + 1):
            if encoded_str[i - 1].isalpha():
                if j > 0 and dp[i - 1][j - 1]:
                    dp[i][j] = True
            elif encoded_str[i - 1] == '?':
                if j > 0 and dp[i - 2][j - 1]:
                    dp[i][j] = True
                if dp[i - 2][j]:
                    dp[i][j] = True
            elif encoded_str[i - 1] == '*':
                if dp[i - 2][j]:
                    dp[i][j] = True
                for l in range(1, j + 1):
                    if dp[i - 2][j - l]:
                        dp[i][j] = True

    if not dp[n][k]:
        return "Impossible"

    result = []
    i, j = n, k
    while i > 0:
        if encoded_str[i - 1].isalpha():
            if j > 0 and dp[i - 1][j - 1]:
                result.append(encoded_str[i - 1])
                i -= 1
                j -= 1
            else:
                i -= 1
        elif encoded_str[i - 1] == '?':
            if j > 0 and dp[i - 2][j - 1]:
                result.append(encoded_str[i - 2])
                i -= 2
                j -= 1
            elif dp[i - 2][j]:
                i -= 2
        elif encoded_str[i - 1] == '*':
            if dp[i - 2][j]:
                i -= 2
            else:
                for l in range(1, j + 1):
                    if dp[i - 2][j - l]:
                        result.extend([encoded_str[i - 2]] * l)
                        i -= 2
                        j -= l
                        break

    return ''.join(result[::-1])

