def can_transform(a, b, c, x, y, z):
    # Calculate the surplus and deficit for each color
    surplus = 0
    deficit = 0

    if a > x:
        surplus += (a - x) // 2
    else:
        deficit += (x - a)

    if b > y:
        surplus += (b - y) // 2
    else:
        deficit += (y - b)

    if c > z:
        surplus += (c - z) // 2
    else:
        deficit += (z - c)

    # Check if the surplus can cover the deficit
    return surplus >= deficit

