def calculate_probability(n, ranges):
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        l_i, r_i = ranges[i - 1]
        for j in range(i + 1):
            dp[i][j] = dp[i - 1][j] * (r_i - l_i + 1) % MOD
            if j > 0:
                dp[i][j] = (dp[i][j] + dp[i - 1][j - 1] * (r_i - l_i + 1) % MOD) % MOD

    total_ways = 1
    for l_i, r_i in ranges:
        total_ways = total_ways * (r_i - l_i + 1) % MOD

    no_inversion_ways = sum(dp[n][i] for i in range(n + 1)) % MOD
    probability = no_inversion_ways * mod_inv(total_ways, MOD) % MOD

    return probability

