def prime_factors(b):
    factors = {}
    d = 2
    while d * d <= b:
        while (b % d) == 0:
            if d in factors:
                factors[d] += 1
            else:
                factors[d] = 1
            b //= d
        d += 1
    if b > 1:
        factors[b] = 1
    return factors

def count_factors_in_factorial(n, p):
    count = 0
    power = p
    while power <= n:
        count += n // power
        power *= p
    return count

def trailing_zeros_in_base(n, b):
    factors = prime_factors(b)
    min_trailing_zeros = float('inf')
    for p in factors:
        count_p = count_factors_in_factorial(n, p)
        min_trailing_zeros = min(min_trailing_zeros, count_p // factors[p])
    return min_trailing_zeros

