def sum_f(m, a, b):
    if a > b:
        a, b = b, a

    gcd_ab = gcd(a, b)
    lcm_ab = (a // gcd_ab) * b

    def f(x):
        if x < a:
            return x + 1
        if x < b:
            return x - (x - a) // gcd_ab
        if x < lcm_ab:
            return x - (x - a) // gcd_ab - (x - b) // gcd_ab + (x - lcm_ab) // gcd_ab
        return x - (x - a) // gcd_ab - (x - b) // gcd_ab + (x - lcm_ab) // gcd_ab + 1

    total_sum = 0
    for i in range(m + 1):
        total_sum += f(i)

    return total_sum

