def is_divisible_by_8(n):
    length = len(n)

    # Check all single digit numbers
    for i in range(length):
        if int(n[i]) % 8 == 0:
            return "YES", n[i]

    # Check all two digit numbers
    for i in range(length):
        for j in range(i + 1, length):
            if int(n[i] + n[j]) % 8 == 0:
                return "YES", n[i] + n[j]

    # Check all three digit numbers
    for i in range(length):
        for j in range(i + 1, length):
            for k in range(j + 1, length):
                if int(n[i] + n[j] + n[k]) % 8 == 0:
                    return "YES", n[i] + n[j] + n[k]

    return "NO", ""

