def min_refuels(a, b, f, k):
    if b < f or b < (a - f):
        return -1

    refuels = 0
    current_fuel = b
    position = 0
    direction = 1  # 1 means going to a, -1 means going to 0

    for journey in range(k):
        if direction == 1:
            distance_to_next = a - position
            if current_fuel < distance_to_next:
                if current_fuel < f:
                    return -1
                refuels += 1
                current_fuel = b - (a - f)
            else:
                current_fuel -= distance_to_next
            position = a
        else:
            distance_to_next = position
            if current_fuel < distance_to_next:
                if current_fuel < (a - f):
                    return -1
                refuels += 1
                current_fuel = b - f
            else:
                current_fuel -= distance_to_next
            position = 0

        direction *= -1

    return refuels

