def can_reach_finish(n, m, s, d, obstacles):
    obstacles.sort()
    current_position = 0
    commands = []

    for i in range(n):
        if obstacles[i] - current_position - 1 >= s:
            run_distance = obstacles[i] - current_position - 1
            if i < n - 1 and obstacles[i + 1] - obstacles[i] - 1 < s:
                if obstacles[i + 1] - current_position - 1 > d:
                    return "IMPOSSIBLE"
                jump_distance = obstacles[i + 1] - current_position - 1
                commands.append(f"RUN {run_distance}")
                commands.append(f"JUMP {jump_distance}")
                current_position = obstacles[i + 1]
            else:
                if run_distance > d:
                    return "IMPOSSIBLE"
                commands.append(f"RUN {run_distance}")
                commands.append(f"JUMP {d}")
                current_position = obstacles[i] + d
        else:
            return "IMPOSSIBLE"

    if current_position < m:
        commands.append(f"RUN {m - current_position}")

    return "\n".join(commands)

