def count_pairs(s, x):
    if (s - x) % 2 != 0 or s < x:
        return 0

    a_plus_b = s
    a_xor_b = x
    a_and_b = (a_plus_b - a_xor_b) // 2

    if a_and_b < 0:
        return 0

    count = 0
    for i in range(40):
        if (a_xor_b & (1 << i)) and (a_and_b & (1 << i)):
            return 0

    for i in range(40):
        if (a_xor_b & (1 << i)):
            count += 1

    return 1 << count

