def min_variables(n, a):
    a.sort()
    if a[0] != 1:
        return -1

    variables = [0] * n
    variables[0] = a[0]
    used = 1

    for i in range(1, n):
        found = False
        for j in range(used):
            for k in range(j, used):
                if variables[j] + variables[k] == a[i]:
                    found = True
                    break
            if found:
                break
        if not found:
            if used < n:
                variables[used] = a[i]
                used += 1
            else:
                return -1

    return used

