def find_max_length(N, S):
    def has_repeated_substring_of_length(length):
        seen = set()
        for i in range(N - length + 1):
            substring = S[i:i + length]
            if substring in seen:
                return True
            seen.add(substring)
        return False

    left, right = 1, N
    max_len = 0

    while left <= right:
        mid = (left + right) // 2
        if has_repeated_substring_of_length(mid):
            max_len = mid
            left = mid + 1
        else:
            right = mid - 1

    return max_len

