    def can_distribute(p):
        left = k - 1
        right = n - k
        total = p
        if left > 0:
            if p > left:
                total += (p - 1) * left - (left * (left - 1)) // 2
            else:
                total += (p * (p - 1)) // 2 + (left - p + 1)
        if right > 0:
            if p > right:
                total += (p - 1) * right - (right * (right - 1)) // 2
            else:
                total += (p * (p - 1)) // 2 + (right - p + 1)
        return total <= m

    low, high = 1, m
    while low < high:
        mid = (low + high + 1) // 2
        if can_distribute(mid):
            low = mid
        else:
            high = mid - 1
    return low

