def find_numbers_with_m_zeroes(m):
    low, high = 0, 5 * (m + 1)
    result = []

    while low <= high:
        mid = (low + high) // 2
        zeroes = count_trailing_zeroes(mid)

        if zeroes < m:
            low = mid + 1
        elif zeroes > m:
            high = mid - 1
        else:
            result = [i for i in range(mid - mid % 5, mid - mid % 5 + 5) if count_trailing_zeroes(i) == m]
