def find_initial_position(n, x):
    # We will reverse the moves to find the initial position
    for i in range(n, 0, -1):
        if i % 2 == 1:
            # Odd move: swap left (0) and middle (1)
            if x == 0:
                x = 1
            elif x == 1:
                x = 0
        else:
            # Even move: swap middle (1) and right (2)
            if x == 1:
                x = 2
            elif x == 2:
                x = 1
    return x

